import React, { useRef,useState } from "react";

function Useref() {
  const data = useRef(null);
  const [name, setName] = useState(false);


const submitForm=(o)=>{
o.preventDefault();
const somename=data.current.value;
somename==""?alert("please enter your name"):setName(true);

}


  return (
    <div>
         <form action=""onSubmit={submitForm}>
           <div>
             <label htmlFor="data">enter your </label>
             <input type="text" id="data" ref={data} />
           </div>
           <br />
           <button className="">submit</button>
         </form>
         <p>{name?`you are so lucky ${data.current.value}`:""}</p>
    </div>
 
  );
}

export default Useref;
