import React from 'react'
import { useState } from 'react';
const FormLogin = () => {
    const [name,setName]=useState("");
    const [all,setAll]=useState([]);
    const submitform=(e)=>{
        e.preventDefault();
        const newdata={name};
        setAll([...all,newdata]);
           setName(" ")
    } 
    
    return (
        <div className="conta">
        <form action="" onSubmit={submitform}>
            <div className="st">
                <label htmlFor="name"><h1>Enter your Name</h1></label><br/>
                <input type="text" name="name" id="name" key="1" value={name}
                onChange={(e)=>setName(e.target.value)} />
            </div>
            <div className="st">  <button type="submit" >Click To Add</button></div>
        </form>
        <div className="st">
       <p>{name}</p>
      {
          all.map((a)=>{
              const {name}=a;
              return(
               <p  >  <span id="a">{name}</span> </p>
              )
          })
      }
         </div>  
        </div>
    )
}

export default FormLogin
