import React from 'react'
import { useRef,useState } from 'react';
const Useref = () => {
    const data=useRef(null);
    const [firstData ,setFirstData]=useState(initialState)("");
    return (
        <div>
            {firstData}
         <input type="text" useRef={data} />
         <input type="button" value="show" onClick={()=>{
             setFirstData(data.current.value)
         }} />
            
        </div>
    )
}

export default Useref
