import React, { useRef, useState } from "react";

function Useref() {
  const txtrf = useRef(null);

  const [name, setName] = useState("");

  return (
    <>
      {name}

      <input type="text" ref={txtrf} />
      <button
        onClick={() => {
          setName(txtrf.current.value);
        }}
      >
        Show
      </button>
    </>
  );
}

export default Useref;
