import React, { useRef, useState } from "react";

function Useref() {
  const data = useRef(null);

  const [name, setName] = useState("");

  return (
    <>
                 <p style={{textAlign:"center"}}>{name}</p> 

    <div className="set"><input type="text" ref={data} /></div>  
    <div className="btn-grad "> <button 
        onClick={() => {
          setName(data.current.value); }}  >   Show  </button>
     </div> </>
  );
}

export default Useref;
